document.addEventListener("DOMContentLoaded",function(){const accordion=document.getElementById("curriculum-accordion");const accordionContent=document.getElementById("curriculum-accordion-content");const links=accordion.querySelectorAll("a");links.forEach(link=>{if(link.href===window.location.href){accordionContent.classList.remove("hidden");accordionContent.style.height=`${accordionContent.scrollHeight}px`;accordion.querySelector(".hs-accordion-toggle").setAttribute("aria-expanded","true");const toggleIconOpen=accordion.querySelector(".hs-accordion-active\\:block");const toggleIconClosed=accordion.querySelector(".hs-accordion-active\\:hidden");if(toggleIconOpen&&toggleIconClosed){toggleIconOpen.classList.remove("hidden");toggleIconOpen.classList.add("block");toggleIconClosed.classList.add("hidden");toggleIconClosed.classList.remove("block");}}});});;