document.getElementById('search-data').addEventListener('input',function(){const searchTerm=this.value.toLowerCase();const tables=document.querySelectorAll('table');tables.forEach((table,tableIndex)=>{const rows=table.querySelectorAll('tbody tr');let hasVisibleRows=false;rows.forEach((row,rowIndex)=>{const cells=row.querySelectorAll('td');let rowContainsSearchTerm=false;if(cells.length>1&&cells[1].textContent.toLowerCase().includes(searchTerm)){rowContainsSearchTerm=true;}
if(cells.length>2&&cells[2].textContent.toLowerCase().includes(searchTerm)){rowContainsSearchTerm=true;}
if(rowContainsSearchTerm){row.style.display='';hasVisibleRows=true;}else{row.style.display='none';}});const noResultsRow=document.getElementById(`no-results-row-${tableIndex + 1}`);if(noResultsRow){if(hasVisibleRows){noResultsRow.style.display='none';}else{noResultsRow.style.display='';}}});});;