document.addEventListener('DOMContentLoaded',function(){document.querySelectorAll('[data-hs-overlay="#edit-course"]').forEach(function(button){button.addEventListener('click',function(){const courseId=this.getAttribute('data-course-id');const courseCode=this.getAttribute('data-course-code');const courseDescription=this.getAttribute('data-course-description');const lecture=this.getAttribute('data-lecture');const laboratory=this.getAttribute('data-laboratory');const creditUnits=this.getAttribute('data-credit-units');const semester=this.getAttribute('data-semester');const yearLevel=this.getAttribute('data-year-level');const isMajorCourse=this.getAttribute('data-is-major-course')==='true';document.getElementById('edit-course-id').value=courseId;document.getElementById('edit-course-code').value=courseCode;document.getElementById('edit-course-description').value=courseDescription;document.getElementById('edit-lecture').value=lecture;document.getElementById('edit-laboratory').value=laboratory;document.getElementById('edit-semester').value=semester;document.getElementById('edit-year-level').value=yearLevel;document.getElementById('edit-is-major-course').checked=isMajorCourse;});});});;