function validateInput(input,min,max){const value=parseInt(input.value,10);console.log(`Validating input: ${input.value}, Min: ${min}, Max: ${max}`);if(isNaN(value)||value<min||value>max){input.classList.add('border-red-500','focus:border-red-500','focus:ring-red-500');input.classList.remove('border-gray-200','focus:border-primary-500','focus:ring-primary-500');}else{input.classList.remove('border-red-500','focus:border-red-500','focus:ring-red-500');input.classList.add('border-gray-200','focus:border-primary-500','focus:ring-primary-500');}}
function attachValidation(){console.log('Attaching validation...');document.querySelectorAll('[id^="laboratory-input-"]').forEach(function(input){console.log('Found laboratory input:',input);input.addEventListener('input',function(){validateInput(this,0,9);});});document.querySelectorAll('[id^="lecture-input-"]').forEach(function(input){console.log('Found lecture input:',input);input.addEventListener('input',function(){validateInput(this,1,9);});});}
attachValidation();;