let isFormDirty=false;function setFormDirty(){isFormDirty=true;}
function handleBeforeUnload(event){if(isFormDirty){const message="You have unsaved changes. Are you sure you want to leave?";event.returnValue=message;return message;}}
document.addEventListener('DOMContentLoaded',()=>{const form=document.querySelector('form');form.addEventListener('input',setFormDirty);form.addEventListener('change',setFormDirty);window.addEventListener('beforeunload',handleBeforeUnload);});;