document.addEventListener('DOMContentLoaded',function(){document.querySelectorAll('[data-hs-overlay="#edit-course"]').forEach(function(button){button.addEventListener('click',function(){const courseId=this.getAttribute('data-course-id');const courseCode=this.getAttribute('data-course-code');const courseDescription=this.getAttribute('data-course-description');const lecture=this.getAttribute('data-lecture');const laboratory=this.getAttribute('data-laboratory');const creditUnits=this.getAttribute('data-credit-units');const semester=this.getAttribute('data-semester');const yearLevel=this.getAttribute('data-year-level');const isMajorCourse=this.getAttribute('data-is-major-course')==='true';document.getElementById('edit-course-id').value=courseId;document.getElementById('edit-course-code').value=courseCode;document.getElementById('edit-course-description').value=courseDescription;document.getElementById('edit-lecture').value=lecture;document.getElementById('edit-laboratory').value=laboratory;document.getElementById('edit-credit-units').value=creditUnits;document.getElementById('edit-semester').value=semester;document.getElementById('edit-year-level').value=yearLevel;document.getElementById('edit-is-major-course').checked=isMajorCourse;});});});;document.addEventListener('DOMContentLoaded',()=>{const form=document.getElementById('edit-course-form');const originalValues={course_code:form.course_code.value,course_description:form.course_description.value,lecture:form.lecture.value,laboratory:form.laboratory.value,credit_units:form.credit_units.value,semester:form.semester.value,year_level:form.year_level.value,is_major_course:form.is_major_course.checked};form.addEventListener('submit',(event)=>{let hasChanges=false;for(const[key,value]of Object.entries(originalValues)){const formElement=form[key];if(formElement.type==='checkbox'){if(formElement.checked!==value){hasChanges=true;break;}}else{if(formElement.value!==value){hasChanges=true;break;}}}
if(!hasChanges){event.preventDefault();alert('No changes detected.');}});});;