document.addEventListener('DOMContentLoaded',function(){const tabButtons=document.querySelectorAll('.tab-button');const yearLevelContents=document.querySelectorAll('.year-level-content');tabButtons.forEach(button=>{button.addEventListener('click',function(){const yearLevel=this.getAttribute('data-year-level');tabButtons.forEach(btn=>btn.classList.remove('active'));yearLevelContents.forEach(content=>content.classList.add('hidden'));this.classList.add('active');document.querySelector(`.year-level-content[data-year-level="${yearLevel}"]`).classList.remove('hidden');});});});;