document.addEventListener('DOMContentLoaded',function(){document.querySelectorAll('[data-hs-overlay="#update-classroom"]').forEach(button=>{button.addEventListener('click',function(){const classroomId=this.getAttribute('data-classroom-id');const classroomName=this.getAttribute('data-classroom-name');const building=this.getAttribute('data-building');const isLab=this.getAttribute('data-is-lab')==='True';document.getElementById('classroom_id2').value=classroomId;document.getElementById('classroom_name2').value=classroomName;document.getElementById('building2').value=building;document.getElementById('hs-xs-switch2').checked=isLab;});});});;