document.addEventListener('DOMContentLoaded',function(){const deleteButton=document.getElementById('delete-button');const classroomIdsInput=document.getElementById('classroom_ids');const deleteForm=document.getElementById('delete-form');document.getElementById('select-all').addEventListener('change',function(){const checkboxes=document.querySelectorAll('.classroom-checkbox');checkboxes.forEach(checkbox=>checkbox.checked=this.checked);});deleteButton.addEventListener('click',function(){const selectedItems=Array.from(document.querySelectorAll('.classroom-checkbox:checked')).map(checkbox=>checkbox.value);if(selectedItems.length>0){classroomIdsInput.value=selectedItems.join(',');const modal=document.getElementById('delete-all-classroom');modal.classList.remove('hidden');modal.classList.add('hs-overlay-open');}else{alert('Please select at least one classroom to delete.');}});document.querySelectorAll('[data-hs-overlay="#delete-all-classroom"]').forEach(button=>{button.addEventListener('click',function(){const modal=document.getElementById('delete-all-classroom');modal.classList.add('hidden');modal.classList.remove('hs-overlay-open');});});});;