document.addEventListener('DOMContentLoaded',function(){const deleteButton=document.getElementById('delete-button');const classroomIdsInput=document.getElementById('classroom_ids');document.getElementById('select-all').addEventListener('change',function(){const checkboxes=document.querySelectorAll('.classroom-checkbox');checkboxes.forEach(checkbox=>checkbox.checked=this.checked);});deleteButton.addEventListener('click',function(){const selectedItems=Array.from(document.querySelectorAll('.classroom-checkbox:checked')).map(checkbox=>checkbox.value);if(selectedItems.length>0){classroomIdsInput.value=selectedItems.join(',');}else{alert('Please select at least one classroom to delete.');}});});;