document.getElementById('search-data').addEventListener('input',function(){const searchTerm=this.value.toLowerCase();const tables=document.querySelectorAll('table');tables.forEach(table=>{const rows=table.querySelectorAll('tbody tr');rows.forEach(row=>{const cells=row.querySelectorAll('td');let rowContainsSearchTerm=false;cells.forEach(cell=>{if(cell.textContent.toLowerCase().includes(searchTerm)){rowContainsSearchTerm=true;}});if(rowContainsSearchTerm){row.style.display='';}else{row.style.display='none';}});});});;