document.addEventListener("DOMContentLoaded",function(){const accordion=document.getElementById("curriculum-accordion");const accordionContent=document.getElementById("curriculum-accordion-content");const links=accordion.querySelectorAll("a");if(localStorage.getItem("curriculumAccordionOpen")==="true"){openAccordion();}
links.forEach(link=>{if(link.href===window.location.href){openAccordion();localStorage.setItem("curriculumAccordionOpen","true");}});function openAccordion(){accordionContent.classList.remove("hidden");accordionContent.style.height=`${accordionContent.scrollHeight}px`;accordion.querySelector(".hs-accordion-toggle").setAttribute("aria-expanded","true");}});;