document.addEventListener('DOMContentLoaded',function(){const filterButton=document.getElementById('hs-as-table-table-filter-dropdown');const selectedCountSpan=document.getElementById('selected-filter-count');const frequencyCheckbox=document.getElementById('hs-as-filters-dropdown-lecture');const createdCheckbox=document.getElementById('hs-as-filters-dropdown-lab');const tableBody=document.getElementById('classroom-table-body');const searchInput=document.getElementById('search-classroom');function updateLabel(){let count=0;if(frequencyCheckbox.checked)count++;if(createdCheckbox.checked)count++;selectedCountSpan.textContent=count>0?count:'';selectedCountSpan.style.display=count>0?'inline-flex':'none';}
function filterTable(){const showLab=createdCheckbox.checked;const showLecture=frequencyCheckbox.checked;const searchTerm=searchInput.value.toLowerCase();const rows=tableBody.querySelectorAll('tr');rows.forEach(row=>{const isLab=row.getAttribute('data-is-lab')==='true';const roomName=row.cells[0].textContent.toLowerCase();const type=row.cells[1].textContent.toLowerCase();const matchesFilter=(showLab&&isLab)||(showLecture&&!isLab)||(!showLab&&!showLecture);const matchesSearch=roomName.includes(searchTerm)||type.includes(searchTerm);if(matchesFilter&&matchesSearch){row.style.display='';}else{row.style.display='none';}});}
function handleFilterChange(){updateLabel();filterTable();}
frequencyCheckbox.addEventListener('change',handleFilterChange);createdCheckbox.addEventListener('change',handleFilterChange);searchInput.addEventListener('input',filterTable);updateLabel();filterTable();});;