document.addEventListener('DOMContentLoaded',function(){const filterButton=document.getElementById('hs-as-table-table-filter-dropdown');const selectedCountSpan=document.getElementById('selected-filter-count');const frequencyCheckbox=document.getElementById('hs-as-filters-dropdown-lecture');const createdCheckbox=document.getElementById('hs-as-filters-dropdown-lab');function updateLabel(){let count=0;if(frequencyCheckbox.checked)count++;if(createdCheckbox.checked)count++;selectedCountSpan.textContent=count>0?count:'';selectedCountSpan.style.display=count>0?'inline-flex':'none';}
frequencyCheckbox.addEventListener('change',updateLabel);createdCheckbox.addEventListener('change',updateLabel);updateLabel();});document.addEventListener('DOMContentLoaded',function(){const labCheckbox=document.getElementById('hs-as-filters-dropdown-lab');const lectureCheckbox=document.getElementById('hs-as-filters-dropdown-lecture');const tableBody=document.getElementById('classroom-table-body');function filterTable(){const showLab=labCheckbox.checked;const showLecture=lectureCheckbox.checked;const rows=tableBody.querySelectorAll('tr');rows.forEach(row=>{const isLab=row.getAttribute('data-is-lab')==='true';if((showLab&&isLab)||(showLecture&&!isLab)){row.style.display='';}else{row.style.display='none';}});}
labCheckbox.addEventListener('change',filterTable);lectureCheckbox.addEventListener('change',filterTable);filterTable();});;