document.addEventListener('DOMContentLoaded',function(){const filterButton=document.getElementById('hs-as-table-table-filter-dropdown');const selectedCountSpan=document.getElementById('selected-filter-count');const frequencyCheckbox=document.getElementById('hs-as-filters-dropdown-lecture');const createdCheckbox=document.getElementById('hs-as-filters-dropdown-lab');const tableBody=document.getElementById('table-body');const noDataMessage=document.getElementById('no-data-message');function updateLabel(){const count=(frequencyCheckbox.checked?1:0)+(createdCheckbox.checked?1:0);selectedCountSpan.textContent=count>0?count:'';selectedCountSpan.style.display=count>0?'inline-flex':'none';}
function filterTable(){const showLab=createdCheckbox.checked;const showLecture=frequencyCheckbox.checked;const rows=tableBody.querySelectorAll('tr:not(#no-results-row)');let hasVisibleRows=false;let noResultsForFilter=true;rows.forEach(row=>{const isLab=row.getAttribute('data-is-lab')==='true';const matchesFilter=(showLab&&isLab)||(showLecture&&!isLab)||(!showLab&&!showLecture);if(matchesFilter){row.style.display='';hasVisibleRows=true;noResultsForFilter=false;}else{row.style.display='none';}});if(noResultsForFilter){noDataMessage.textContent=`No results found`;}else{noDataMessage.textContent='';}
document.getElementById('no-results-row').style.display=hasVisibleRows?'none':'';}
function handleFilterChange(){updateLabel();filterTable();}
frequencyCheckbox.addEventListener('change',handleFilterChange);createdCheckbox.addEventListener('change',handleFilterChange);updateLabel();filterTable();});;